mapper.insert:
SELECT IFNULL(MAX(BDD_SEQ),0)+1 AS BDD_SEQ FROM TB_BOARD_DATA
/* saveBoard */
INSERT INTO TB_BOARD_DATA ( BDD_SEQ, BDS_SEQ, BDD_USER, BDD_WRITER, BDD_TITLE, BDD_CONTENT, BDD_UPLOAD, BDD_REPLY_LASTDATE, BDD_GROUP, BDD_NOTICE, BDD_DATE_WORKB, BDD_DATE_WORKE, BDD_PROJECT, BDD_INCHARGE, BDD_CENTER, BDD_TARGET, BDD_DATA_TYPE, BDD_STATE, BDD_CDATE ) VALUES ( #{bddSeq}, #{bdsSeq}, #{bddUser}, #{bddWriter}, #{bddTitle}, #{bocontents}, #{bddUpload}, #{bddReplyLastdate}, #{bddGroup}, #{bddNotice}, #{bddDateWorkb}, #{bddDateWorke}, #{bddProject}, #{bddIncharge}, #{bddCenter}, #{bddTarget}, #{bddDataType}, #{bddState}, NOW() )

mapper.update:

/* updateBoard */

UPDATE TB_BOARD_DATA SET
	  BDD_TITLE = #{bddTitle}
	, BDD_CONTENT = #{bocontents}
	, BDD_DATE_MAGAM = #{bddDateMagam}
	, BDD_GROUP = #{bddGroup}
	, BDD_NOTICE = #{bddNotice}
	, BDD_DATE_WORKB = #{bddDateWorkb}
	, BDD_DATE_WORKE = #{bddDateWorke}
	, BDD_TARGET = #{bddTarget}
	, BDD_DATA_TYPE = #{bddDataType}
WHERE BDD_SEQ = #{bddSeq}


/* deleteBoard */

UPDATE TB_BOARD_DATA SET 
	BDD_STATE ='D'
WHERE BDD_SEQ = #{bddSeq}

mapper.delete:

/* fileDeleteBoard */

DELETE	TB_UPLOAD_FILE
WHERE	UFL_SEQ = #{seqnum} and UFL_FILESIZE = #{chknum}


/* uploadfileDeleteCenterFileBoard */

DELETE	TB_BOARD_DATA_CENTER_FILE
WHERE	FILES_SEQ = #{seqnum} and BDDC_CENTER = #{bddcCenter}

mapper.select:

SELECT	CODE_CODE AS CD_NO, CODE_NAME AS CD_NM, CODE_SUBCODE AS CDSUB 
FROM	TB_CODE
WHERE	CODEG_CODE = #{sCode}
		AND	CODE_USE_YN = 'Y'


	AND CODE_CODE IN ('1', '2', '3'
		
		, '7'
		
	)


	AND SUBSTR(CODE_CODE, 1, 1) = #{gCode}


ORDER BY CODE_SORT

mapper.update:

/* changeBoardDataState */

UPDATE TB_BOARD_DATA SET
	BDD_STATE = #{bddState}
	, BDD_IP = #{bddIp}
WHERE BDD_SEQ = #{bddSeq}

mapper.select:

/* isDataExist */

SELECT	COUNT(*)
FROM	TB_BOARD_DATA
WHERE	BDD_SEQ = #{bddSeq}

mapper.delete:

