	BDDC_CENTER_USER = #{userId}
	, BDDC_CENTER_DATE = now()
WHERE BDD_SEQ = #{bddSeq} 
	AND BDDC_CENTER = #{bddcCenter}

mapper.select:

/* searchAutoSave */

SELECT NVL((SELECT MAX(BDD_SEQ) FROM TB_BOARD_DATA WHERE BDD_STATE='A' AND BDD_USER=#{userId}), 0 ) AS BDD_SEQ
FROM DUAL

mapper.delete:

/* deleteAutoSave */

DELETE	
FROM	TB_BOARD_DATA
WHERE	BDD_SEQ = #{bddSeq}
		AND BDD_USER = #{userId}

mapper.select:
/* boardUploadCenterList */
SELECT * FROM ( SELECT BDC.BDD_SEQ, BDC.BDDC_CENTER, FN_GET_CODE_NAME('CM05', BDC.BDDC_CENTER) AS BDDC_CENTER_NAME, '' AS BDCF_ETC, FU_GETUSER_NM(BDCF_WRITER_USER) AS BDCF_WRITER_USER, BDCF_CDATE , BDCF_CHECK_DATE, BDCF_CHECK_USER, FILES_SEQ, IFNULL((SELECT UFL_FILESIZE FROM TB_UPLOAD_FILE WHERE UFL_SEQ = FILES_SEQ), 0) AS FILES_SIZE , CD.CODE_SORT, FU_GETUSER_NM(BDDC_CENTER_USER) AS BDDC_CENTER_USER, BDDC_CENTER_DATE , BDCF_MEMO, CD.CODE_GROUP, CD.CODE_NAME, FN_GET_CODE_NAME('CM04', CD.CODE_GROUP) AS CGROUP_NM FROM TB_BOARD_DATA_CENTER BDC LEFT OUTER JOIN TB_BOARD_DATA_CENTER_FILE BDCF ON BDC.BDD_SEQ = BDCF.BDD_SEQ AND BDC.BDDC_CENTER = BDCF.BDDC_CENTER, TB_CODE CD WHERE BDC.BDDC_CENTER = CD.CODE_CODE AND CODEG_CODE = 'CM05' AND BDC.BDD_SEQ = #{bddSeq}
AND BDC.BDDC_CENTER = #{bddcCenter}
) a ORDER BY CODE_GROUP, CODE_SORT, CODE_NAME DESC


/* boardReplyList */

SELECT	BDR_SEQ, BDR_USER, '작성자' as BDR_USER_NAME, BDR_CONTENT, BDR_CDATE, BDR_UPLOAD, '무명' as BDR_USER_CENTER
FROM	TB_BOARD_REPLY
WHERE	BDD_SEQ = #{bddSeq}

mapper.delete:

/* fileDelete */

DELETE	
FROM	TB_UPLOAD_FILE
WHERE	UFL_SEQ = #{seqnum}
		AND UFL_FILESIZE = #{chknum}
		AND UFL_REL_NUMBER = #{bddSeq}


/* fileDeleteCenterFile */

DELETE	
FROM	TB_BOARD_DATA_CENTER_FILE
WHERE	FILES_SEQ = #{seqnum}
		AND BDDC_CENTER = #{bddcCenter}
		AND BDD_SEQ = #{bddSeq}

